function average(values){
    let sum = 0;
    let length = values.length;
    for(let i = 0; i < length; i++) {
        sum += values[i];
    }
    return sum / length;
}

function differences(values) {
    let avg = average(values);
    let diffs = values.map(function(value) {
        let diff = value - avg;
        return diff;
    });
    return diffs;
}

function square(values) {
    let sqrs = values.map(function(value) {
        let sqr = value ** 2;
        return sqr;
    });
    return sqrs;
}

function standarDeviation(values){
    const n = values.length;
  if (n === 0) {
    return NaN;
  }
  const mean = values.reduce((sum, x) => sum + x, 0) / n;
  const sumSqDiff = values.reduce((sum, x) => sum + (x - mean) ** 2, 0);
  return Math.sqrt(sumSqDiff / n);
};

module.exports.standarDeviation = standarDeviation;
module.exports.average = average;