function average(values){
    let sum = values.reduce(function(sum, value) {
        return sum + value;
    },0);

    let avg = sum / values.lenght;
    return avg;
}

function differences(values) {
    let avg = average(values);
    let diffs = values.map(function(value) {
        let diff = value - avg;
        return diff;
    });
    return diffs;
}